if (response.code === 200) {
  const body = JSON.parse(response.body);
  if (body.taxes && body.taxes.length > 0) {
    const current = body.taxes.find(t => t.tax_year === 2025);
    const prior = body.taxes.find(t => t.tax_year === 2024);
    if (current) environment.set('tax_id_current_year', current.id);
    if (prior) environment.set('tax_id_prior_year', prior.id);
  }
}
